import React from 'react'
import { render } from 'react-dom';
import { Card, CardHeader, CardBody, CardFooter } from 'react-simple-card';
import './RouteBoard.css'
import DatePicker from 'react-datepicker'


class BoardRoute extends React.Component {

    state = {
        userName: this.props.user,
        stage: [],
        stageItem: '',
        toggle: true,
        present: new Date(),


    }
    handlePresent = (date) => {
        this.setState({ present: date })
    }
    stageEdit = (e) => {
        this.setState({ stageItem: e.target.value })
    }
    stageField = () => {
        this.setState({ toggle: false })
    }
    addStage = () => {
        let localStorageData = JSON.parse(localStorage.getItem(this.state.userName))
        localStorageData.boards[this.props.boardkey].stages.push({


            stageName: this.state.stageItem,
            tasks: [
                {
                    title: '',
                    desc: '',
                    startTime: '',
                    endTime: ''
                }
            ]


        })

        localStorage.setItem(this.state.userName, JSON.stringify(localStorageData))
        this.setState({ toggle: true })

    }

    removeStages = (pkey) => {

        let localStorageData = JSON.parse(localStorage.getItem(this.state.userName))
        localStorageData.boards[this.props.boardkey].stages.splice(pkey)
        console.log("removeStages"+localStorageData.boards[this.props.key])
    }

    render() {
        let localStorageData = JSON.parse(localStorage.getItem(this.state.userName))
        console.log(this.props.boardname)
        return (
            <div>
                <button onClick={this.props.boardlist}>GO BACK</button>
                You are on <b>{this.props.boardname}</b>
                {this.state.toggle ? <button onClick={this.stageField}>Add Stages</button> : <span><input type='text' onChange={this.stageEdit}></input><button onClick={this.addStage}>ADD</button></span>}

                <div>



                    <p>{localStorageData.boards[this.props.boardkey].stages.map((item, key) => (<Card className='stages'><CardHeader>{item.stageName}<button onClick={this.removeStages(key)}><b>X</b></button></CardHeader><CardBody><input type='text' placeholder='descriptione here'>  <DatePicker className="form-control" placeholder="date" onChange={this.handlePresent} selected={this.state.present} value={this.state.present} /></input><br/></CardBody><CardFooter><button>Add Task</button></CardFooter></Card>))}</p>
                    {/* {console.log(localStorageData.boards[this.props.boardkey])} */}

                </div>
            </div>)
    }

}

export default BoardRoute
