import React from 'react'
import { render } from 'react-dom';
import { Card, CardHeader, CardBody, CardFooter } from 'react-simple-card';
import './RouteBoard.css'

class BoardRoute extends React.Component
{
    state={
        stage:[],
        stageItem:'',
        toggle:true
    }
stage
    addStage=()=>
    {
        let localStorageData = JSON.parse(localStorage.getItem(this.state.username))
        localStorageData.stages.push({


                    stageName: '',
                    tasks: [
                        {
                            title: '',
                            desc: '',
                            startTime: '',
                            endTime: ''
                        }
                    ]


        })

        localStorage.setItem(this.state.username, JSON.stringify(localStorageData))

    }

    render()
    {
        console.log(this.props.boardname)
    return (
        <div>
        <button onClick={this.props.boardlist}>BACK</button>
        You are on <b>{this.props.boardname}</b> 
        {this.state.toggle?<button >Add Stages</button>:<div><input type='text' onChange={this.stageField}></input><button >ADD</button></div>}

        <div>

        </div>
        <div>
        <Card className='stages'>
        <CardHeader>NEW</CardHeader>
        <CardBody>
        <p>task 1</p>
        <p>task 2</p>
        </CardBody>
        <CardFooter><button>Add Task</button></CardFooter>
        </Card>
        <Card className='stages'>
        <CardHeader>DONE</CardHeader>
        <CardBody>
        <p>task 1</p>
        <p>task 2</p>
        </CardBody>
        <CardFooter><button>Add Task</button></CardFooter>
        </Card>

        </div>
        </div>)
    }

}

export default BoardRoute








