import React from 'react'
// import Dropdown from 'react-dropdown';
// import 'react-dropdown/style.css';

class Board extends React.Component {
    state = {
        username: this.props.name,
        board: [],
        stages: '',
        toggle: false,
        submit: true

    }
    addBoard = () => {

        this.setState({ submit: false, toggle: true })
    }
    nameBoard = (e) => {
        this.setState({ stages: e.target.value })
    }
    addBoardItem = () => {
        let localStorageData = JSON.parse(localStorage.getItem(this.state.name));
        let obj = {

        }
        // localStorageData.boards.boardName=this.state.stages
        localStorageData.setItem(this.state.username,localStorageData)        
        this.state.board.splice(this.state.board.length, 0, this.state.stages)
        this.setState({ submit: true, toggle: false })
        console.log(this.state.board)
    }


    render() {
        return (
            <div>
                <div>
                    BOARD:


                    <div>

                        {this.state.toggle ? <div><input type='text' onChange={this.nameBoard}></input>
                            <button onClick={this.addBoardItem}>ADD</button></div> : this.state.submit ?

                                <p>{this.state.board.map((stage, index) => (<li>{stage}</li>))}</p>

                                : null}

                        <button onClick={this.addBoard}>ADD NEW BOARD</button>
                    </div>



                </div>
            </div>
        )
    }
}

export default Board;
