import React from 'react'
import { render } from 'react-dom';
import { Card, CardHeader, CardBody, CardFooter } from 'react-simple-card';
import './RouteBoard.css'


class BoardRoute extends React.Component {

    state = {
        userName: this.props.user,
        stage: [],
        stageItem: '',
        toggle: true,
        removeDefault1: true,
        removeDefault2: true
    }
    removeDefaultStages1 = () => {
        this.setState({ removeDefault1: false })
    }
    removeDefaultStages2 = () => {
        this.setState({ removeDefault2: false })
    }
    stageEdit = (e) => {
        this.setState({ stageItem: e.target.value })
    }
    stageField = () => {
        this.setState({ toggle: false })
    }
    addStage = () => {
        let localStorageData = JSON.parse(localStorage.getItem(this.state.userName))
        localStorageData.boards[this.props.boardkey].stages.push({


            stageName: this.state.stageItem,
            tasks: [
                {
                    title: '',
                    desc: '',
                    startTime: '',
                    endTime: ''
                }
            ]


        })

        localStorage.setItem(this.state.userName, JSON.stringify(localStorageData))
        this.setState({ toggle: true })

    }

    removeStages = (key) => {
        let localStorageData = JSON.parse(localStorage.getItem(this.state.userName))
        localStorageData.boards[this.props.boardkey].stages.splice(key)
    }

    render() {
        let localStorageData = JSON.parse(localStorage.getItem(this.state.userName))
        console.log(this.props.boardname)
        return (
            <div>
                <button onClick={this.props.boardlist}>BACK</button>
                You are on <b>{this.props.boardname}</b>
                {this.state.toggle ? <button onClick={this.stageField}>Add Stages</button> : <span><input type='text' onChange={this.stageEdit}></input><button onClick={this.addStage}>ADD</button></span>}

                <div>
                    {this.state.removeDefault1 ?
                        <Card className='stages'>
                            <CardHeader>NEW<span><button onClick={this.removeDefaultStages1}><b>X</b></button></span></CardHeader>
                            <CardBody>
                                <p>task 1</p>
                                <p>task 2</p>
                            </CardBody>
                            <CardFooter><button>Add Task</button></CardFooter>
                        </Card> : null}

                    {this.state.removeDefault2 ?
                        <Card className='stages'>
                            <CardHeader>DONE<span><button onClick={this.removeDefaultStages2}><b>X</b></button></span></CardHeader>
                            <CardBody>
                                <p>task 1</p>
                                <p>task 2</p>
                            </CardBody>
                            <CardFooter><button>Add Task</button></CardFooter>
                        </Card> : null}


                    <p>{localStorageData.boards[this.props.boardkey].stages.map((item, key) => (<Card className='stages'><CardHeader>{item.stageName}<button onClick={this.removeStages(key)}><b>X</b></button></CardHeader><CardBody>DEFAULT TASK</CardBody><CardFooter><button>Add Task</button></CardFooter></Card>))}</p>
                    {/* {console.log(localStorageData.boards[this.props.boardkey])} */}

                </div>
            </div>)
    }

}

export default BoardRoute
