import React from 'react'
// import Dropdown from 'react-dropdown';
// import 'react-dropdown/style.css';

class Board extends React.Component {
    state = {
        username: this.props.name,
        board: [],
        boardItem: '',
        toggle: false,
        submit: true

    }
    addBoard = () => {

        this.setState({ submit: false, toggle: true })
    }
    nameBoard = (e) => {
        this.setState({ stages: e.target.value })
    }
    addBoardItem = () => {
        // let localStorageData = null;
        // let obj = {
        //     board: this.state.boardItem,            
        // }
        // localStorageData = JSON.parse(localStorage.getItem(this.state.username));

        // if(localStorageData.board===null)
        // {
        //     localStorage.setItem(this.state.username,obj)
        // }



        this.state.board.splice(this.state.board.length, 0, this.state.boardItem)
        this.setState({ submit: true, toggle: false })
        console.log(this.state.board)
    }


    render() {
        return (
            <div>
                <div>
                    BOARD:


                    <div>

                        {this.state.toggle ? <div><input type='text' onChange={this.nameBoard}></input>
                            <button onClick={this.addBoardItem}>ADD</button></div> : this.state.submit ?

                                <p>{this.state.board.map((item, index) => (<li>{item}</li>))}</p>

                                : null}

                        <button onClick={this.addBoard}>ADD NEW BOARD</button>
                    </div>



                </div>
            </div>
        )
    }
}

export default Board;
