import React from "react";
import { slide as Menu } from "react-burger-menu";
import './styles.css'
import { Route, Switch, Link } from 'react-router-dom'
import Board from './Boards'
import Reports from './Reports'

class Sidebar extends React.Component {
    render() {
        return (
            <div>
                <Menu >
                    <Link to="/report">REPORT</Link>
                    <Link to="/board">BOARDS</Link>

                </Menu>
                <label className="HelloUser"><h2>HELLO USER,  {this.props.name}</h2></label>

                <button onClick={this.props.onLogout} style={{ backgroundColor: "61dafb", color: "#282c34" }} className="LogoutBtn">LOGOUT</button>
                <Switch>
                    <Route path="/board">
                        <Board name={this.props.name} style={{s}} />
                    </Route>
                    <Route path="/report">
                        <Reports username={this.props.name} />
                    </Route>
                </Switch>

            </div>
        );
    }
};
export default Sidebar
