import React from 'react'
// import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';
// import {BrowserRouter as Router ,Route,Switch,Link} from 'react-router-dom'


// class List extends React.Component
// {

// }

class Board extends React.Component
{
state={

    board:[],
    boardItem:'',
    toggle:false

}
    addBoard()
    {
        this.setState({toggle:true})
    }    
    nameBoard = (e) => {
        this.setState({ boardItem: e.target.value })
    }
    addBoardItem()
    {
        this.state.board.splice()
    }


    render()
    {
        return(
            <div>
                <div>
                    BOARD:
                    <div>{this.state.toggle?<div><input type='text' onChange={this.nameBoard}></input>
                    <button onClick={addBoardItem}>ADD</button></div>:null}
                    <button onClick={this.addBoard}>ADD NEW BOARD</button>
                    </div>                   



            </div>
            </div>
        )
    }
}

export default Board;
